var connection=(new signalR.HubConnectionBuilder).withUrl("/vlxd_Hub").configureLogging(signalR.LogLevel.Information).build();connection.start().catch(n=>console.error(n.toString()));connection.on("ReceiveMessage",n=>{var r=$("#announcement-template").html(),t=Mustache.render(r,{Content:n.content,Id:n.id,Title:n.title,Avatar:n.image,DateCreated:moment(n.DateCreated).fromNow()}),i;parseInt($("#totalAnnouncement").text())===0&&(t+=$("#announcement-tag-template").html());$("#announcementList").prepend(t);i=parseInt($("#totalAnnouncement").text())+1;$("#totalAnnouncement").text(i)});